    <!-- JS Implementing Plugins -->
    <script>
        // Image Preview
        const imageInput = document.getElementById('image-input');
        const imagePreview = document.getElementById('image-preview');

        imageInput.addEventListener('change', function() {
            const file = this.files[0];
            if (file) {
                const reader = new FileReader();
                reader.onload = function(event) {
                    imagePreview.innerHTML =
                        `<img src="${event.target.result}" class="w-full h-auto rounded-lg" alt="Course Image Preview">`;
                }
                reader.readAsDataURL(file);
            }
        });

        // Add Chapter and Assignment Functionality
        document.addEventListener('DOMContentLoaded', function() {
            // Add a new chapter
            document.getElementById('add-chapter').addEventListener('click', function() {
                const chapterList = document.getElementById('chapter-list');
                const chapterCount = chapterList.querySelectorAll('.chapter').length;

                const newChapter = document.createElement('div');
                newChapter.className =
                    'chapter bg-gray-100 p-4 rounded-lg border border-gray-200 space-y-2';
                newChapter.innerHTML =
                    `<div class="flex justify-between items-center">
                    <input type="text" name="chapters[${chapterCount}]" placeholder="New Chapter" class="w-full p-2 border border-gray-300 rounded-lg input-focus">
                </div>
                <div class="quiz-list space-y-2"></div>
                <div class="assignment-list space-y-2"></div>
                <button type="button" class="mt-2 text-indigo-600 font-medium hover:text-indigo-800 transition add-quiz-btn">Add a Quiz</button>
                <button type="button" class="mt-2 text-indigo-600 font-medium hover:text-indigo-800 transition add-assignment-btn">Add an Assignment</button>`;

                chapterList.appendChild(newChapter);

                attachQuizButton(newChapter.querySelector('.add-quiz-btn'));
                attachAssignmentButton(newChapter.querySelector('.add-assignment-btn'));
            });

            // Attach functionality to the "Add a Quiz" buttons
            function attachQuizButton(button) {
                button.addEventListener('click', function() {
                    const quizList = this.previousElementSibling;
                    const chapterIndex = [...document.querySelectorAll('.chapter')].indexOf(this.closest(
                        '.chapter'));
                    const quizCount = quizList.querySelectorAll('.quiz').length;

                    const newQuiz = document.createElement('div');
                    newQuiz.className =
                        'quiz bg-blue-100 p-2 rounded-lg flex justify-between items-center border border-blue-200';
                    newQuiz.innerHTML =
                        `<input type="text" name="quizzes[${chapterIndex}][]" placeholder="Quiz: Example Quiz" class="w-full p-1 border border-blue-200 rounded-lg input-focus">
                        <button type="button" class="text-red-500 ml-2 remove-quiz-btn">Delete</button>`;
                    quizList.appendChild(newQuiz);
                });
            }

            // Attach functionality to the "Add an Assignment" buttons
            function attachAssignmentButton(button) {
                button.addEventListener('click', function() {
                    const assignmentList = this.previousElementSibling.previousElementSibling;
                    const chapterIndex = [...document.querySelectorAll('.chapter')].indexOf(this.closest(
                        '.chapter'));
                    const assignmentCount = assignmentList.querySelectorAll('.assignment').length;

                    const newAssignment = document.createElement('div');
                    newAssignment.className =
                        'assignment bg-green-100 p-2 rounded-lg flex justify-between items-center border border-green-200';
                    newAssignment.innerHTML = `
                    <input type="text" name="assignments[${chapterIndex}][]" placeholder="Assignment: Example Assignment" class="w-full p-1 border border-green-200 rounded-lg input-focus">
                    <button type="button" class="text-red-500 ml-2 remove-assignment-btn">Delete</button>`;
                    assignmentList.appendChild(newAssignment);
                });
            }

            // Attach delete functionality to quizzes, assignments, and chapters
            document.addEventListener('click', function(e) {
                if (e.target && e.target.classList.contains('remove-quiz-btn')) {
                    const quizId = e.target.getAttribute('data-quiz-id');
                    if (quizId) {
                        let deletedQuizzes = document.getElementById('deleted-quizzes').value;
                        deletedQuizzes += quizId + ',';
                        document.getElementById('deleted-quizzes').value = deletedQuizzes;
                    }
                    e.target.closest('.quiz').remove();
                }
                if (e.target && e.target.classList.contains('remove-assignment-btn')) {
                    const assignmentId = e.target.getAttribute('data-assignment-id');
                    if (assignmentId) {
                        let deletedAssignments = document.getElementById('deleted-assignments').value;
                        deletedAssignments += assignmentId + ',';
                        document.getElementById('deleted-assignments').value = deletedAssignments;
                    }
                    e.target.closest('.assignment').remove();
                }
                if (e.target && e.target.classList.contains('delete-chapter-btn')) {
                    const chapterId = e.target.getAttribute('data-chapter-id');
                    if (chapterId) {
                        let deletedChapters = document.getElementById('deleted-chapters').value;
                        deletedChapters += chapterId + ',';
                        document.getElementById('deleted-chapters').value = deletedChapters;
                    }
                    e.target.closest('.chapter').remove();
                }
            });

            // Attach assignment button functionality to existing chapters
            document.querySelectorAll('.add-assignment-btn').forEach(attachAssignmentButton);

            // Attach quiz and assignment button functionality to existing chapters
            document.querySelectorAll('.add-quiz-btn').forEach(attachQuizButton);

            // Sortable functionality for chapters, quizzes, and assignments
            new Sortable(document.getElementById('chapter-list'), {
                animation: 150,
                handle: '.chapter',
                onEnd: function() {
                    updateInputNames();
                }
            });

            function updateInputNames() {
                const chapters = document.querySelectorAll('.chapter');
                chapters.forEach((chapter, chapterIndex) => {
                    const chapterInput = chapter.querySelector('input[name^="chapters"]');
                    chapterInput.name = `chapters[${chapterIndex}]`;

                    const quizzes = chapter.querySelectorAll('.quiz input[name^="quizzes"]');
                    quizzes.forEach((quizInput, quizIndex) => {
                        quizInput.name = `quizzes[${chapterIndex}][${quizIndex}]`;
                    });

                    const assignments = chapter.querySelectorAll('.assignment input[name^="assignments"]');
                    assignments.forEach((assignmentInput, assignmentIndex) => {
                        assignmentInput.name = `assignments[${chapterIndex}][${assignmentIndex}]`;
                    });
                });
            }
        });
    </script>
